function form_date(a){var b=["Minggu","Senin","Selasa","Rabu","Kamis","Jum'at","Sabtu"],c=["Januari","Februari","Maret","April","Mei","Juni","Juli","Agustus","September","Oktober","November","Desember"],d=new Date(a).getDate(),e=b[new Date(a).getDay()],f=c[new Date(a).getMonth()],g=new Date(a).getYear(),h=g<1e3?g+1900:g,i=e+", "+d+" "+f+" "+h;return i}